function transform(payload, bucketId, userId, headers) {
  const eventType = payload.eventType || payload.type || 'unknown';
  const message = payload.message || payload.body || JSON.stringify(payload);
  const timestamp = payload.timestamp || new Date().toISOString();

  let deliveryType = 'NORMAL';
  if (payload.severity === 'critical' || payload.severity === 'error') {
    deliveryType = 'HIGH_PRIORITY';
  } else if (payload.severity === 'warning') {
    deliveryType = 'NORMAL';
  } else {
    deliveryType = 'SILENT';
  }

  return {
    title: `Webhook: ${eventType}`,
    subtitle: `Received at ${new Date(timestamp).toLocaleString()}`,
    body: message,
    deliveryType: deliveryType
  };
}

function transform(payload, bucketId, userId, headers) {
  let logData = payload;

  if (typeof payload === 'string') {
    try {
      logData = JSON.parse(payload);
    } catch (e) {
      return {
        title: 'Log Entry',
        body: payload,
        deliveryType: 'NORMAL'
      };
    }
  }

  const level = logData.level || logData.severity || 'INFO';
  const message = logData.message || logData.msg || JSON.stringify(logData);
  const timestamp = logData.timestamp || logData.time || new Date().toISOString();

  let deliveryType = 'NORMAL';
  if (level === 'ERROR' || level === 'FATAL' || level === 'CRITICAL') {
    deliveryType = 'HIGH_PRIORITY';
  } else if (level === 'WARN' || level === 'WARNING') {
    deliveryType = 'NORMAL';
  } else {
    deliveryType = 'SILENT';
  }

  return {
    title: `[${level}] Log Entry`,
    subtitle: new Date(timestamp).toLocaleString(),
    body: message,
    deliveryType: deliveryType
  };
}
